package com.uknown.firstsubmission.uiimport android.content.Contextimport androidx.appcompat.app.AppCompatDelegateimport androidx.test.core.app.ActivityScenarioimport androidx.test.core.app.ApplicationProviderimport androidx.test.espresso.Espresso.onViewimport androidx.test.espresso.action.ViewActions.*import androidx.test.espresso.assertion.ViewAssertions.matchesimport androidx.test.espresso.matcher.ViewMatchers.*import androidx.test.internal.runner.junit4.AndroidJUnit4ClassRunnerimport com.uknown.firstsubmission.Rimport org.junit.Assert.*import org.junit.Beforeimport org.junit.Testimport org.junit.runner.RunWith@RunWith(AndroidJUnit4ClassRunner::class)class MainActivityTest {    val resultDark = AppCompatDelegate.MODE_NIGHT_YES    val resultLight = AppCompatDelegate.MODE_NIGHT_NO    lateinit var context: Context    @Before    fun setup() {        ActivityScenario.launch(MainActivity::class.java)        context = ApplicationProvider.getApplicationContext()    }    @Test    fun onLightMode() {        onView(withId(R.id.darkAction)).check(matches(isDisplayed()))        onView(withId(R.id.darkAction)).perform(click())        if (AppCompatDelegate.getDefaultNightMode() == AppCompatDelegate.MODE_NIGHT_NO) {            assertEquals(resultLight, AppCompatDelegate.getDefaultNightMode())        } else {            assertEquals(resultDark, AppCompatDelegate.getDefaultNightMode())        }    }}