package com.uknown.firstsubmission.localimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabase@Database(entities = [User::class], version = 1, exportSchema = false)abstract class DatabaseDao : RoomDatabase() {    abstract fun service(): ServiceDao    companion object {        private var instance: DatabaseDao? = null        fun getInstance(context: Context): DatabaseDao = instance ?: synchronized(this) {            instance ?: Room.databaseBuilder(                context.applicationContext,                DatabaseDao::class.java,                "Favorite Database"            ).build()        }    }}