package com.uknown.firstsubmission.localimport android.os.Parcelableimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKeyimport kotlinx.parcelize.Parcelize@Entity@Parcelizedata class User(    @PrimaryKey(false)    @ColumnInfo("username")    var username: String,    @ColumnInfo("avatarUrl")    var avatarUrl: String?,) : Parcelable