package com.uknown.firstsubmission.network.retorfitimport com.uknown.firstsubmission.BuildConfigimport okhttp3.Interceptorimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryclass ApiConfiguration {    companion object {        fun getApiService(): Service {            val loggingInterceptor = Interceptor {                val req = it.request()                val requestHeader = req.newBuilder()                    .addHeader("Authorization", "token ${BuildConfig.KEY}")                    .build()                it.proceed(requestHeader)            }            val client = OkHttpClient.Builder()                .addInterceptor(loggingInterceptor)                .build()            val retrofit = Retrofit.Builder()                .baseUrl(BuildConfig.BASE_URL)                .addConverterFactory(GsonConverterFactory.create())                .client(client)                .build()            return retrofit.create(Service::class.java)        }    }}