package com.uknown.firstsubmission.network.retorfitimport com.uknown.firstsubmission.network.response.ItemsItemimport com.uknown.firstsubmission.network.response.UserDetailResponseimport com.uknown.firstsubmission.network.response.UserResponseimport retrofit2.Callimport retrofit2.http.GETimport retrofit2.http.Pathimport retrofit2.http.Queryinterface Service {    @GET("search/users")    fun searchUser(        @Query("q") username: String    ): Call<UserResponse>    @GET("users/{username}")    fun getDetailUser(        @Path("username") username: String    ): Call<UserDetailResponse>    @GET("users/{username}/followers")    fun getFollowers(        @Path("username") username: String    ): Call<List<ItemsItem>>    @GET("users/{username}/following")    fun getFollowing(        @Path("username") username: String    ): Call<List<ItemsItem>>}