package com.uknown.firstsubmission.ui.adapterimport android.os.Bundleimport androidx.appcompat.app.AppCompatActivityimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterimport com.uknown.firstsubmission.ui.fragment.FollowFragmentclass SectionAdapter(activity: AppCompatActivity, val username: String) : FragmentStateAdapter(activity) {    override fun getItemCount(): Int = 2    override fun createFragment(position: Int): Fragment {        val fragment = FollowFragment()        fragment.arguments = Bundle().apply {            putInt(FollowFragment.SECTION, position+1)            putString(FollowFragment.SECTION2, username)        }        return fragment    }}