package com.uknown.firstsubmission.ui.adapterimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.ListAdapterimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.uknown.firstsubmission.databinding.ItemListUserBindingimport com.uknown.firstsubmission.network.response.ItemsItemclass UserListAdapter (val onClick : (ItemsItem) -> Unit) : ListAdapter<ItemsItem, UserListAdapter.ViewHolder>(DIFFUTIL) {    companion object {        val DIFFUTIL = object : DiffUtil.ItemCallback<ItemsItem>(){            override fun areItemsTheSame(oldItem: ItemsItem, newItem: ItemsItem): Boolean {                return newItem == oldItem            }            override fun areContentsTheSame(oldItem: ItemsItem, newItem: ItemsItem): Boolean {                return newItem == oldItem            }        }    }    class ViewHolder (val binding: ItemListUserBinding) : RecyclerView.ViewHolder(binding.root){        fun bind(item: ItemsItem) {            binding.tvUsername.text = item.login            Glide.with(binding.root.context)                .load(item.avatarUrl)                .into(binding.image)        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): UserListAdapter.ViewHolder {        return ViewHolder(ItemListUserBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: UserListAdapter.ViewHolder, position: Int) {        holder.bind(getItem(position))        holder.itemView.setOnClickListener { onClick(getItem(position)) }    }}