package com.uknown.firstsubmission.ui.viewmodelimport androidx.lifecycle.MediatorLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.uknown.firstsubmission.network.response.UserDetailResponseimport com.uknown.firstsubmission.repository.DetailRepositoryimport com.uknown.firstsubmission.utils.Resourcesimport kotlinx.coroutines.launchclass DetailViewModel(    private val detailRepository: DetailRepository) : ViewModel() {    private val _detailData = MediatorLiveData<Resources<UserDetailResponse?>>()    val detailUser = _detailData    fun getDetailUser(username: String) = viewModelScope.launch {        _detailData.value = detailRepository.getDetailUser(username).value    }    fun getFollowers(username: String) = detailRepository.getFollowers(username)    fun getFollowing(username: String) = detailRepository.getFollowing(username)}