package com.uknown.firstsubmission.utilsimport android.content.Contextimport com.uknown.firstsubmission.local.DatabaseDaoimport com.uknown.firstsubmission.network.retorfit.ApiConfigurationimport com.uknown.firstsubmission.repository.DetailRepositoryimport com.uknown.firstsubmission.repository.MainRepositoryobject Injection {    fun mainRepository(context: Context): MainRepository? {        val database = DatabaseDao.getInstance(context)        val serviceDao = database.service()        val service = ApiConfiguration.getApiService()        val appExecutor = AppExecutor()        val pref = SettingPreference.getInstatnce(context.datastore)        return pref?.let { MainRepository.getInstance(service, serviceDao, appExecutor, it) }    }    fun detailRepository(context: Context): DetailRepository? {        val service = ApiConfiguration.getApiService()        return DetailRepository.getInstance(service)    }}