package com.uknown.firstsubmission.utilsimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.uknown.firstsubmission.repository.DetailRepositoryimport com.uknown.firstsubmission.repository.MainRepositoryimport com.uknown.firstsubmission.ui.viewmodel.DetailViewModelimport com.uknown.firstsubmission.ui.viewmodel.MainViewModelclass ViewModelFactory(val repository: Any) : ViewModelProvider.NewInstanceFactory() {    @Suppress("UNCHECKED_CAST")    override fun <T : ViewModel> create(modelClass: Class<T>): T {        if (modelClass.isAssignableFrom(MainViewModel::class.java)) {            return MainViewModel(repository as MainRepository) as T        } else if (modelClass.isAssignableFrom(DetailViewModel::class.java)) {            return DetailViewModel(repository as DetailRepository) as T        }        throw IllegalArgumentException("Can\'t instance viewmodel")    }}