package com.uknown.firstsubmission.ui.viewmodelimport androidx.lifecycle.MutableLiveDataimport com.uknown.firstsubmission.local.ServiceDaoimport com.uknown.firstsubmission.local.Userimport org.junit.Assertimport org.junit.Testimport org.mockito.Mockito.mockimport org.mockito.Mockito.`when`class MainViewModelTest {    @Test    fun checkDatabase() {        val data = MutableLiveData(User("ispamungkas", "ispamungkas"))        val serviceDao = mock(ServiceDao::class.java)        `when`(serviceDao.getDataUser("ispamungkas")).thenReturn(data)        val hasil = serviceDao.getDataUser("ispamungkas")        Assert.assertEquals(User("ispamungkas", "ispamungkas"), hasil.value)    }}